import Btn from "./Btn";
import { useEffect, useState } from "react";

function Hello() {
  useEffect(() => {
    console.log("nice to meet you :) ");
    return console.log("good bye :(");
  }, []);
  return <h1>Hello</h1>;
}

function App() {
  const [value, setValue] = useState(0);
  const cntup = () => {
    setValue((now) => now + 1);
  };
  const [showing, setShowing] = useState(false);

  const show = () => {
    setShowing((now) => !now);
  };

  return (
    <div>
      <h1>Welcome Back!!!!</h1>
      <Btn />
      <div>
        <br />
        <br />
        <button onClick={cntup}>count up</button>
        <h3>{value}</h3>
      </div>
      {showing ? <Hello /> : null}
      <button onClick={show}>{showing ? "hide" : "show"}</button>
    </div>
  );
}

export default App;
